package main

import (
	"math"
	"sort"
)

type Node struct {
	WordIDX  int
	Childs   map[rune]*Node
	minChild rune
}

func suggestedProducts(products []string, searchWord string) [][]string {
	// 27 means that there is not childs
	root := &Node{-1, map[rune]*Node{}, math.MaxInt32}
	// sort words to search the nearest words faster
	sort.StringSlice(products).Sort()
	// 1 build trie from an input
	for i, product := range products {
		first := root
		for j, c := range product {
			if root.Childs[c] == nil {
				root.Childs[c] = &Node{-1, map[rune]*Node{}, math.MaxInt32}
				if product[:j+1] == products[i] {
					root.Childs[c].WordIDX = i
				}
			}
			if c < root.minChild {
				root.minChild = c
			}

			root = root.Childs[c]
		}
		root = first
	}

	// 2 create prefix starting from first letter to the last letter and return search suggestion as a []string
	result := [][]string{}

	for i, c := range searchWord {
		result = append(result, []string{})

		if root.Childs[c] != nil {
			root = root.Childs[c]
		}
		// 2.1 From this point search for a word with minimal lexographic ordering
		subRoot := root

		for subRoot.minChild < math.MaxInt32 && subRoot.WordIDX < 0 {
			// Go the the next child with minimal
			subRoot = subRoot.Childs[subRoot.minChild]
		}

		// 2.2  create a suggesting for typed letter
		minIDX := subRoot.WordIDX
		for len(result[i]) < 3 && minIDX < len(products) && i < len(products[minIDX]) && products[minIDX][0:i+1] == searchWord[0:i+1] {
			result[i] = append(result[i], products[minIDX])
			minIDX++
		}
		// if suggestion list is empty we can fill left elemts of suggestion array by empty arrays
		if len(result[i]) == 0 {
			for ; i < len(searchWord)-1; i++ {
				result = append(result, []string{})
			}
			return result
		}

	}
	return result
}

